import React, { useEffect, useRef } from 'react';
import './Map.scss';


const Map = ({stops, center}) => {
  const google = window.google;
  const googleMap = useRef()


  useEffect(() => {
    initMap(stops, center)
  }, [center])


  function initMap(stops, center) {

    const map = new google.maps.Map(googleMap.current, {
      zoom: 15,
      center: center,
    });

    stops.forEach(stop => {
      const infowindow = new google.maps.InfoWindow({
        content:
          "<h5 id='stop_name'>`Stop Name: ${stop.stop_name}`</h5>" +
          '<p id="stop_description">stop.stop_desc</p>',
      });
      const marker = new google.maps.Marker({
        position: {lng: stop.stop_lon, lat: stop.stop_lat},
        map,
        title: stop.name,
      });

      marker.addListener("click", () => {
        infowindow.open({
          anchor: marker,
          map,
          shouldFocus: false,
        });
      });
    })
  }

  window.initMap = initMap

  return (
    <div className={'main-map d-flex justify-content-center align-items-center flex-column'}>
      <div className="jumbotron w-100 bg-light">
        <div className="container">
          <h1 className="display-3">STOPS MAP</h1>
          <p>All stops that defined on redux are here, on the Google Maps, with Google Map Info Windows.</p>
        </div>
      </div>
      <div id="map" ref={googleMap} className={'google-map'}></div>
    </div>
  );
};

export default Map;
