import React, { useEffect, useRef } from 'react';
import './Map.scss';
let center = {lng:29.94146137, lat: 40.76001855}
const Map = ({stops}) => {

  const googleMap = useRef()

  useEffect(() => {
    initMap(stops,center)
  }, [])

  const google = window.google;

  function initMap(stops, center) {

    const map = new google.maps.Map(googleMap.current, {
      zoom:14,
      center: center,
    });

    stops.forEach(stop => {
      const infowindow = new google.maps.InfoWindow({
        content: stop.stop_desc,
      });
      const marker = new google.maps.Marker({
        position: {lng: stop.stop_lon, lat: stop.stop_lat},
        map,
        title: stop.name,
      });

      marker.addListener("click", () => {
        infowindow.open({
          anchor: marker,
          map,
          shouldFocus: false,
        });
      });
    })
  }

  window.initMap = initMap

  return (
    <div>
      <button onClick={()=>initMap(stops,{lng:29.947529, lat: 40.765312})}></button>
      <div id="map" ref={googleMap} className={'google-map'}></div>
    </div>
  );
};

export default Map;
